import React, { Component } from 'react';
// import PropTypes from 'prop-types';


class User extends Component {
    state = {
        isVisible: true
    }
    onClickEvent = () =>{
        console.log("tıklandı...");
        this.setState({
            isVisible: !this.state.isVisible
        })
    }
    
    // arrow function olarak tanımlarsak otomatik bind() işlemi yapılmış olur.


    render () {

        // Destructing özelliği normalde this.props.name diye tanımlamıştık 
       
        return(
           <div className = "col-md-8 mb-4">
               <div className = "card-header d-flex justify-content-between">
                    <h4  className = "d-inline">{this.props.name}</h4>
                    <i onClick = {this.onClickEvent} className = "far fa-trash-alt" style ={{cursor: "pointer"}} ></i>
               </div>
                {this.state.isVisible ?
                  <div className = "card-body">
                  <p className = "card-text">Department: {this.props.department}</p>
                  <p className = "card-text">Sallery: {this.props.sallery}</p>
              </div> : null   
            
                } 
           
           </div>
           
        )
    }
}
User.defaultProps = {
    department: "Unemployed"
}

// User.propTypes = {
//     name: PropTypes.string.isRequired,
//     department: PropTypes.string.isRequired,
//     skill: PropTypes.string.isRequired
// }

export default User;
