import React, { Component } from 'react';
import PropTypes from 'prop-types';


class User extends Component {
    constructor(props){
        super(props)
        this.state = {
            isVisible: true
        }
    }
    onClickEvent() {
        console.log("tıklandı...");
        this.setState({
            isVisible: !this.state.isVisible
        })
    }
    
    // arrow function olarak tanımlarsak otomatik bind() işlemi yapılmış olur.


    render () {

        // Destructing özelliği normalde this.props.name diye tanımlamıştık 
        const {name, department, skill} = this.props;
        return(
            <div>
                 <h2 onClick = {this.onClickEvent}> {name} <i className="far fa-trash-alt"></i> </h2>
                   {/* .bind(this) fonksiyonunu kullanarak oluşturduğumuz metodu bağlıyoruz.
                    kendi metodlarımızda this anahtar kelimesi class direkt olarak göstermiyor */}
                {this.state.isVisible ? <ul>
                    <li>Deparment = {department}</li>
                    <li>Skill ={skill}</li>
                </ul> : null}
                
            </div>
        )
    }
}
User.defaultProps = {
    department: "Unemployed"
}

User.propTypes = {
    name: PropTypes.string.isRequired,
    department: PropTypes.string.isRequired,
    skill: PropTypes.string.isRequired
}

export default User;
